static bool mvt_tag_is_primary(enum mvt_tag tag)
{
	switch (tag) {
	case MVT_DEPOSIT:
		return true;
	case MVT_WITHDRAWAL:
		return true;
	case MVT_PENALTY:
		return true;
	case MVT_INVOICE:
		return true;
	case MVT_ROUTED:
		return true;
	case MVT_PUSHED:
		return true;
	case MVT_CHANNEL_OPEN:
		return true;
	case MVT_CHANNEL_CLOSE:
		return true;
	case MVT_CHANNEL_TO_US:
		return true;
	case MVT_HTLC_TIMEOUT:
		return true;
	case MVT_HTLC_FULFILL:
		return true;
	case MVT_HTLC_TX:
		return true;
	case MVT_TO_WALLET:
		return true;
	case MVT_ANCHOR:
		return true;
	case MVT_TO_THEM:
		return true;
	case MVT_PENALIZED:
		return true;
	case MVT_STOLEN:
		return true;
	case MVT_TO_MINER:
		return true;
	case MVT_OPENER:
		return false;
	case MVT_LEASE_FEE:
		return true;
	case MVT_LEASED:
		return false;
	case MVT_STEALABLE:
		return false;
	case MVT_CHANNEL_PROPOSED:
		return true;
	case MVT_SPLICE:
		return false;
	}
	abort();
}

int main(int argc, char *argv[])
{
	common_setup(argv[0]);
	for (size_t i = 0; i < NUM_MVT_TAGS; i++) {
		if (mvt_tag_is_primary(i)) {
			struct mvt_tags tags;
			assert((1ULL << i) & PRIMARY_TAG_BITS);
			tags.bits = (1ULL << i);
			assert(primary_mvt_tag(tags) == i);
		} else {
			assert(((1ULL << i) & PRIMARY_TAG_BITS) == 0);
		}
	}
	common_shutdown();
}
