const char *input;

static void check_string(jsmntok_t *t, const char *expected)
{
	assert(t->type == JSMN_STRING);
	assert(t->size == 0);
	assert(strncmp(input + t->start, expected, t->end - t->start) == 0);
}

static void check_primitive(jsmntok_t *t, const char *expected)
{
	assert(t->type == JSMN_PRIMITIVE);
	assert(t->size == 0);
	assert(strncmp(input + t->start, expected, t->end - t->start) == 0);
}

static void test_conversion(void)
{
	const char *err;
	struct list_head *tokens;
	struct graphql_executable_document *doc;
	struct graphql_field *field1;
	jsmntok_t *params;

	input = "{ field1(strarg1: \"value\", intarg1: 4, floatarg1: 3.5, boolarg1: false, nullarg1: null, enumarg1: value) }";
	err = graphql_lexparse(input, NULL, &tokens, &doc);
	assert(err == 0);
	field1 = doc->first_def->op_def->sel_set->first->field;

	convert_args_to_paramtokens(field1, NULL, &params);

	assert(params[0].type == JSMN_OBJECT);
	assert(params[0].size == 6);
	check_string(&params[1], "strarg1");
	check_string(&params[2], "value");
	check_string(&params[3], "intarg1");
	check_primitive(&params[4], "4");
	check_string(&params[5], "floatarg1");
	check_primitive(&params[6], "3.5");
	check_string(&params[7], "boolarg1");
	check_primitive(&params[8], "false");
	check_string(&params[9], "nullarg1");
	check_primitive(&params[10], "null");
	check_string(&params[11], "enumarg1");
	check_string(&params[12], "value");
}


int main(int argc, char *argv[])
{
	common_setup(argv[0]);

	test_conversion();

	common_shutdown();
}
