static void remove_file(char *fname) { assert(!remove(fname)); }

int main(int argc, char *argv[])
{
	int fd;
	char *gossfile;
	struct gossmap *gossmap;
	struct node_id l1, l2, l3;
	struct short_channel_id scid12, scid23;
	struct gossmap_localmods *mods;
	struct gossmap_chan *chan;

	common_setup(argv[0]);

	fd = tmpdir_mkstemp(tmpctx, "run-not_mcf-diamond.XXXXXX", &gossfile);
	tal_add_destructor(gossfile, remove_file);
	assert(write_all(fd, canned_map, sizeof(canned_map)));

	gossmap = gossmap_load(tmpctx, gossfile, NULL);
	assert(gossmap);

	/* These are in ascending order, for easy direction setting */
	assert(node_id_from_hexstr("03b0cb87d0fc736a628deeb26e688afa0bb5cfc9dac339ab8c57d80d1be8b9d47d", 66, &l1));
	assert(node_id_from_hexstr("02155ebdd54ee27ab0091505b6589ef2741593250420bd797efc066c91fe9d50af", 66, &l2));
	assert(node_id_from_hexstr("0266e4598d1d3c415f572a8488830b60f7e744ed9235eb0b1ba93283b315c03518", 66, &l3));

	// this channel is in the gossmap
	assert(short_channel_id_from_str("336x1x2", 7, &scid12));
	// this channel is not
	assert(short_channel_id_from_str("1000x2x0", 8, &scid23));

	mods = gossmap_localmods_new(tmpctx);

	// these are the properties of the channel in the gossmap
	int dir = 0;
	u32 enabled = 1;
	bool enabled_bool = enabled;
	u32 base_fee = 1;
	u32 proportional_fee = 10;
	u16 delay = 6;
	struct amount_msat htlc_min = AMOUNT_MSAT(0);
	struct amount_msat htlc_max = AMOUNT_MSAT(1089000000);
	fp16_t htlc_min_fp =
	    u64_to_fp16(htlc_min.millisatoshis, /* Raw: to fp16 */
			false);
	fp16_t htlc_max_fp =
	    u64_to_fp16(htlc_max.millisatoshis, /* Raw: to fp16 */
			true);

	// try changing some of them

	// change the htlc_min and enabled properties only
	htlc_min = AMOUNT_MSAT(300);
	htlc_min_fp =
	    u64_to_fp16(htlc_min.millisatoshis, /* Raw: to fp16 */
			false);
	enabled = 0;
	enabled_bool = enabled;

	assert(gossmap_local_updatechan_selected(mods,
						 scid12,
						 &htlc_min,
						 NULL, // htlc_max
						 NULL, // base fee
						 NULL, // proportional fee
						 NULL, // delay
						 &enabled_bool,
						 dir,
						 gossmap));

	// check
	gossmap_apply_localmods(gossmap, mods);
	chan = gossmap_find_chan(gossmap, &scid12);
	assert(chan);
	assert(chan->half[dir].base_fee==base_fee);
	assert(chan->half[dir].proportional_fee==proportional_fee);
	assert(chan->half[dir].enabled==enabled);
	assert(chan->half[dir].delay==delay);
	assert(chan->half[dir].htlc_min==htlc_min_fp);
	assert(chan->half[dir].htlc_max==htlc_max_fp);
	gossmap_remove_localmods(gossmap, mods);

	// change the base_fee
	base_fee = 100;
	assert(gossmap_local_updatechan_selected(mods,
						 scid12,
						 NULL, // htlc_min
						 NULL, // htlc_max
						 &base_fee,
						 NULL, // proportional fee
						 NULL, // delay
						 NULL, // enabled
						 dir,
						 gossmap));

	// change the proportional_fee
	proportional_fee = 321;
	assert(gossmap_local_updatechan_selected(mods,
						 scid12,
						 NULL, // htlc_min
						 NULL, // htlc_max
						 NULL, // base fee
						 &proportional_fee,
						 NULL, // delay
						 NULL, // enabled
						 dir,
						 gossmap));

	// check
	gossmap_apply_localmods(gossmap, mods);
	chan = gossmap_find_chan(gossmap, &scid12);
	assert(chan);
	assert(chan->half[dir].base_fee==base_fee);
	assert(chan->half[dir].proportional_fee==proportional_fee);
	assert(chan->half[dir].enabled==enabled);
	assert(chan->half[dir].delay==delay);
	assert(chan->half[dir].htlc_min==htlc_min_fp);
	assert(chan->half[dir].htlc_max==htlc_max_fp);
	gossmap_remove_localmods(gossmap, mods);

	// now let's add a hidden channel
	dir = 0;
	enabled = 1;
	enabled_bool = enabled;
	base_fee = 0;
	proportional_fee = 45;
	delay = 10;
	htlc_min = AMOUNT_MSAT(1000);
	htlc_max = AMOUNT_MSAT(1000000);
	htlc_min_fp = u64_to_fp16(htlc_min.millisatoshis, /* Raw: to fp16 */
				  false);
	htlc_max_fp = u64_to_fp16(htlc_max.millisatoshis, /* Raw: to fp16 */
				  true);

	assert(gossmap_local_addchan(mods, &l2, &l3, scid23, NULL));
	assert(gossmap_local_updatechan(mods, scid23,
					htlc_min,
					htlc_max,
					base_fee,
					proportional_fee,
					delay,
					enabled,
					dir));

	// check
	gossmap_apply_localmods(gossmap, mods);
	chan = gossmap_find_chan(gossmap, &scid23);
	assert(chan);
	assert(chan->half[dir].base_fee==base_fee);
	assert(chan->half[dir].proportional_fee==proportional_fee);
	assert(chan->half[dir].enabled==enabled);
	assert(chan->half[dir].delay==delay);
	assert(chan->half[dir].htlc_min==htlc_min_fp);
	assert(chan->half[dir].htlc_max==htlc_max_fp);
	gossmap_remove_localmods(gossmap, mods);

	// make some changes
	base_fee = 999;
	assert(gossmap_local_updatechan_selected(mods,
						 scid23,
						 NULL, // htlc_min
						 NULL, // htlc_max
						 &base_fee, // base fee
						 NULL, // proportional fee
						 NULL, // delay
						 NULL, // enabled
						 dir,
						 gossmap));

	// make some changes
	delay = 50;
	enabled = enabled ^ 1;
	enabled_bool = enabled;
	assert(gossmap_local_updatechan_selected(mods,
						 scid23,
						 NULL, // htlc_min
						 NULL, // htlc_max
						 NULL, // base fee
						 NULL, // proportional fee
						 &delay, // delay
						 &enabled_bool, // enabled
						 dir,
						 gossmap));

	// check
	gossmap_apply_localmods(gossmap, mods);
	chan = gossmap_find_chan(gossmap, &scid23);
	assert(chan);
	assert(chan->half[dir].base_fee==base_fee);
	assert(chan->half[dir].proportional_fee==proportional_fee);
	assert(chan->half[dir].enabled==enabled);
	assert(chan->half[dir].delay==delay);
	assert(chan->half[dir].htlc_min==htlc_min_fp);
	assert(chan->half[dir].htlc_max==htlc_max_fp);
	gossmap_remove_localmods(gossmap, mods);

	common_shutdown();
}
